const KEY = "f84fc31d"

export default function App() {
  const [query, setQuery] = useState("interstellar");
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("")
  // const tempQuery = "interstellar";
  const [selectedId, setSelectedId] = useState("")

  function handelSelectID(id) {
    setSelectedId(id)
  }
  useEffect(function () {

    async function fetchMovie() {
      try {
        setIsLoading(true);
        setError(null)
        const respons = await fetch(`http://www.omdbapi.com/?apikey=${KEY}&s=${query}`)
        if (!respons.ok) {
          throw new Error("Something went wrong while fetching movies!")
        }


        const data = await respons.json();

        if (data.Response === "False" || data.Search.length === 0) {
          throw new Error("No movies found for the search query.")

        }
        setMovies(data.Search)
        setIsLoading(false);
      } catch (error) {
        setError(error.message)
      } finally {
        setIsLoading(false)
      }

    }
    if (query.length < 2) {
      setMovies([]);
      setError("");
    }
    fetchMovie();
  }, [query]);


  return (
    <>
      <NavBar >
        <Search query={query} setQuery={setQuery} />
        <NumResults movies={movies} />
      </NavBar>

      <Main>
        <Box>
          {isLoading && <Loader />}
          {!isLoading && !error && <MovieList movies={movies} onHandelClick={handelSelectID} />}
          {error && <Error message={error} />}

        </Box>
        <Box >
          {selectedId ? <MovieDetails selectedId={selectedId} />
            :
            <>
              <WatchedSummary watched={watched} />
              <WatchedList watched={watched} />
              </> }

        </Box>
      </ Main>
    </>
  );
}

