const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);
const KEY = "f84fc31d"

export default function App() {
  const [query, setQuery] = useState("interstellar");
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("")
  // const tempQuery = "interstellar";
  const [selectedId, setSelectedId] = useState("")

  function handelSelectID(id) {
    setSelectedId((curentId) => id === curentId ? null : id)
  }
  function handelClosMovie() {
    setSelectedId(null)
  }

  function handelAddWatched(movie) {
    setWatched(watched => [...watched, movie]);
  }

  function handelDeletWatched(id) {
    setWatched(watched => watched.filter(movie => movie.imdbID !== id))
  }

  useEffect(function () {
    const controller = new AbortController

    async function fetchMovie() {
      try {
        setIsLoading(true);
        setError(null)

        const respons = await fetch(`http://www.omdbapi.com/?apikey=${KEY}&s=${query}`, { signal: controller.signal })

        if (!respons.ok) {
          throw new Error("Something went wrong while fetching movies!")
        }


        const data = await respons.json();

        if (data.Response === "False" || data.Search.length === 0) {
          throw new Error("No movies found for the search query.")

        }
        setMovies(data.Search)
        setIsLoading(false);

      } catch (error) {
        console.log(error.message)
        setError(error.message)
      } finally {
        setIsLoading(false)
      }

    }
    if (query.length < 2) {
      setMovies([]);
      setError("");
    }
    fetchMovie();

    return function () {
      controller.abort();
    };
  },
    [query]);


  return (
    <>
      <NavBar >
        <Search query={query} setQuery={setQuery} />
        <NumResults movies={movies} />
      </NavBar>

      <Main>
        <Box>
          {isLoading && <Loader />}
          {!isLoading && !error && <MovieList
            movies={movies}
            onHandelClick={handelSelectID}
          />}

          {error && <Error message={error} />}

        </Box>
        <Box >
          {selectedId ? <MovieDetails
            selectedId={selectedId}
            onCloseClick={handelClosMovie}
            onAddWatched={handelAddWatched}
            watched={watched}
          />
            :
            <>
              <WatchedSummary watched={watched} />
              <WatchedList watched={watched} handelDeletWatched={handelDeletWatched} />
            </>}

        </Box>
      </ Main>
    </>
  );
}

function Loader() {
  return <p className="loader">Loaing ...</p>
}

function Error({ message }) {
  return (
    <p>🚨{message}</p>
  )
}
function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  )
}

function Search({ query, setQuery }) {


  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
    />
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">🍿</span>
      <h1>usePopcorn</h1>
    </div>
  );
}

function NumResults({ movies }) {
  return (
    <p className="num-results">
      Found <strong>{movies.length}</strong> results
    </p>
  )
}

function Main({ children }) {
  return (
    <main className="main">
      {children}
    </main>
  )
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button
        className="btn-toggle"
        onClick={() => setIsOpen((open) => !open)}
      >
        {isOpen ? "–" : "+"}
      </button>
      {isOpen && children}
    </div>
  );
}


function MovieList({ movies, onHandelClick }) {


  return (
    <ul className="list list-movies">
      {movies?.map((movie) => (
        <Movie movie={movie} key={movie.imdbID} onHandelClick={onHandelClick} />
      ))}
    </ul>
  );
}

function Movie({ movie, onHandelClick }) {
  return (
    <li onClick={() => onHandelClick(movie.imdbID)} >

      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <h3>{movie.Title}</h3>
      <div>
        <p>
          <span>🗓</span>
          <span>{movie.Year}</span>
        </p>
      </div>
    </li>
  )
}

function MovieDetails({ selectedId, onCloseClick, onAddWatched, watched }) {

  const [movie, setMovie] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [userRating, setUserRating] = useState('')

  const isWatched = watched.map((movie) => movie.imdbID).includes(selectedId)

  const {
    Title: title,
    Year: year,
    Genre: genre,
    Poster: poster,
    Runtime: runtime,
    Released: released,
    Director: director,
    Actors: actors,
    Plot: plot,
    imdbRating } = movie;

  function handelAdd() {

    const newAddMovieWatched = {
      imdbID: selectedId,
      title,
      year,
      poster,
      imdbRating: Number(imdbRating),
      runtime: Number(runtime.split(" ").at(0)),
      userRating,


    }

    onAddWatched(newAddMovieWatched)
    onCloseClick()
  }



  useEffect(() => {

    async function getMovieDetails() {
      setIsLoading(true);
      const res = await fetch(`http://www.omdbapi.com/?apikey=${KEY}&i=${selectedId}`);
      const data = await res.json();
      setMovie(data);
      setIsLoading(false);


    }
    getMovieDetails();
  }, [selectedId]);

  useEffect(
    function () {
      if (!title) return;
      document.title = `Movie | ${title}`;
    }, [title]
  );

  return (
    <div className="details">
      {isLoading ? <Loader /> :
        <>
          <header>
            <button className="btn-back" onClick={onCloseClick}>
              &larr; Back</button>
            <img src={poster} aria-colcount={`poster of ${movie}`} />
            <div className="details-overview">
              <h2>{title}</h2>
              <p>
                {released} &bull; {runtime}
              </p>
              <p>{genre}</p>
              <p>
                <span>⭐</span>
                {imdbRating}
              </p>
            </div>
          </header>
          <section>
            <div className="rating">
              {!isWatched ? (
                <>
                  <StartRating maxRating={10} size={24} onSetRating={setUserRating} />

                  {userRating > 0 && (
                    <button className="btn-add"
                      onClick={handelAdd} >+ Add To List</ button>)}
                </>
              ) : (
                <p>you rated </p>
              )

              }
            </div>
            <p>
              <em>{plot}</em>
            </p>
            <p>Starring {actors}</p>
            <p>Dircted by {director}</p>
          </section>
        </>
      }
    </div>

  );
}

function WatchedList({ watched, handelDeletWatched }) {
  return (
    <ul className="list">
      {watched.map((movie) => (
        <WatchedMovie movie={movie} key={movie.imdbID} handelDeletWatched={handelDeletWatched} />
      ))}
    </ul>
  );
}

function WatchedMovie({ movie, handelDeletWatched }) {
  return (
    <li key={movie.imdbID}>
      <img src={movie.poster} alt={`${movie.title} poster`} />
      <h3>{movie.title}</h3>
      <div>
        <p>
          <span>⭐️</span>
          <span>{movie.imdbRating}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{movie.userRating}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{movie.runtime} min</span>
        </p>
        <button className="btn-delete" onClick={() => handelDeletWatched(movie.imdbID)}>X</button>
      </div>
    </li>
  )
}

function WatchedSummary({ watched }) {
  const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));

  return (
    <div className="summary">
      <h2>Movies you watched</h2>
      <div>
        <p>
          <span>#️⃣</span>
          <span>{watched.length} movies</span>
        </p>
        <p>
          <span>⭐️</span>
          <span>{avgImdbRating.toFixed(2)}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{avgUserRating.toFixed(2)}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{avgRuntime} min</span>
        </p>
      </div>
    </div>
  );
}

